create table SMPRIMARY
(
    HPC_IS_MASS            VARCHAR2,
    SB_ROOT_INCIDENT       VARCHAR2,
    NUMBER                 VARCHAR2 not null,
    PRIORITY_CODE          VARCHAR2,
    HPC_ASSIGNEE_NAME      VARCHAR2,
    HPC_AFFECTED_ITEM_NAME VARCHAR2,
    PLAN_OPEN              VARCHAR2,
    PLAN_END               VARCHAR2,
    HPC_STATUS             VARCHAR2,
    PROM                   VARCHAR2,
    ACTION                 VARCHAR2
);

create unique index SMPRIMARY_NUMBER_UINDEX
    on SMPRIMARY (NUMBER);

